" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Approach to the patient with colonic polyps",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
