var title_f31_62_32748="Promotion of general knowledge";
var content_f31_62_32748=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F70285&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F70285&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Suggestions for enhancing a child's cognitive and general knowledge skills before kindergarten entry",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Parents can promote cognitive",
"skills by asking their children to place information or objects* into",
"categories according to:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Function",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Size",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Color",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Shape",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Kind",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Parents can enhance",
"their child's arithmetic skills by teaching them to:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Count by rote to 100",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Understand number concepts to 10",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Comprehend the concept of 'each'",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Identify a circle, triangle, and square",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Comprehend the concepts of",
"'more than, less than, same'",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        \"Identify which group has",
"more/less/the",
"same amount\"",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Parents can help to",
"increase their children's knowledge of general, musical, artistic,",
"scientific, historical, and literary information by:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Visiting museums, exhibits, zoos, and dramatic theatre",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Listening to audio-books-on-tape",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Viewing videos, television, and",
"illustrated books with their child",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Discussing what they see or hear as",
"they take walks or drive along roadways",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        The following",
"instructional experiences may be beneficial in helping children learn",
"to explain information precisely, respond to questions completely, and",
"increase attention to details:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Describe the attributes and",
"functions of a familiar object or objects",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Respond to specific questions",
"related to the name, color, size, use, etc of the object",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Respond to the",
"following questions related to situations, pictures of familiar",
"objects:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        What can you tell me about this?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        What else can you tell me",
"about it?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        What more can you think of to",
"tell me about it?",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Are there any",
"other things about it that are important?",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Eg, toys, laundry, groceries, etc.&nbsp;",
"    </div>",
"    <div class=\"reference\">",
"     Adapted in part from recommendations compiled by staff members of the Learning Support Center, Texas Children's Hospital.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
