" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Subcutaneous and intramuscular immune globulin therapy",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?31/9/31896/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?31/9/31896/contributors\">",
"     Melvin Berger, MD, PhD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?31/9/31896/contributors\">",
"     Section Editor",
