"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#H24\">",
"      NONSPECIFIC HEADACHE AND STROKE RISK",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#H25\">",
"      TREATMENT",
"     </a>",
"    </li>",
"    <li class=\"bulletItem\">",
"     <a class=\"outlineLink\" href=\"#H26\">",
