var title_f32_11_32955="Osteoporosis prevention in COPD";
var content_f32_11_32955=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F70743&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F70743&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Recommendations to decrease osteoporosis risk in COPD",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Measure BMD in the following high-risk patients at baseline:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Those on chronic oral glucocorticoids or high-dose inhaled glucocorticoids",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Postmenopausal women",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Premenopausal women with amenorrhea",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Hypogonadal men",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        History of fracture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        BMI &lt;22",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Follow BMD every 6 to 12 mo in those receiving oral glucocorticoids or every 12 to 24 mo in those not taking oral glucocorticoids",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Give supplements to ensure daily intake of 1000 to 1500 mg calcium and 400 to 800 IU vitamin D",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Encourage an exercise program to improve strength and balance",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Offer gonadal hormone replacement to all postmenopausal women, premenopausal women with amenorrhea, and hypogonadal men (unless contraindicated)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Consider bisphosphonates or calcitonin in patients with osteoporosis or in high-risk patients in whom HRT is not effective or indicated",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from Biskobing DM. COPD and osteoporosis. Chest 2002; 121:609. Copyright &copy; 2002 American College of Chest Physicians.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
