var title_f32_14_33004="Chemicals requiring specific decontamination";
var content_f32_14_33004=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F70216&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F70216&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Selected chemicals requiring specific decontamination",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Toxin",
"      </td>",
"      <td class=\"subtitle1\">",
"       Classification",
"      </td>",
"      <td class=\"subtitle1\">",
"       Treatment",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Bitumen (Tar)",
"      </td>",
"      <td>",
"       Caustic",
"      </td>",
"      <td>",
"       <p>",
"        Copious irrigation with cold water until bitumen cools and hardens.",
"       </p>",
"       <p>",
"        Bitumen that is adherent to blistered skin should be removed with blister epithelium. Bitumen adherent to unblistered skin should be covered liberally with a hydrocarbon solvent (eg, mineral oil).",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Chromic acid",
"      </td>",
"      <td>",
"       Caustic with systemic toxicity",
"      </td>",
"      <td>",
"       Standard decontamination with consideration of 10 percent ascorbic acid added to irrigant.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Hydrofluoric acid",
"      </td>",
"      <td>",
"       Caustic with systemic toxicity",
"      </td>",
"      <td>",
"       <p>",
"        Standard decontamination followed by application of 10 percent calcium gluconate gel. Parenteral calcium administration by injection or venous or arterial infusion may be required.",
"       </p>",
"       <p>",
"        Patient at risk for hypocalcemia.",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Lime (calcium oxide)",
"      </td>",
"      <td>",
"       Caustic",
"      </td>",
"      <td>",
"       Brush off as much as possible prior to contact with water.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Methyl mercury",
"      </td>",
"      <td>",
"       Caustic with systemic toxicity",
"      </td>",
"      <td>",
"       Standard decontamination with blister debridement and blister fluid removal.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Phenol",
"      </td>",
"      <td>",
"       Caustic with systemic toxicity",
"      </td>",
"      <td>",
"       Irrigate with polyethylene glycol (PEG) 400.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Phosphorus (elemental yellow phosphorus)",
"      </td>",
"      <td>",
"       Caustic",
"      </td>",
"      <td>",
"       <p>",
"        Avoid exposure to air.",
"       </p>",
"       <p>",
"        Copious water irrigation, and keep covered with water.",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Radiation",
"      </td>",
"      <td>",
"       Acute radiation syndrome",
"      </td>",
"      <td>",
"       <p>",
"        Obtain radiation monitoring device.",
"       </p>",
"       <p>",
"        Protect personnel if patient is radioactive.",
"       </p>",
"       <p>",
"        Decontaminate from periphery to center of area of exposure. Avoid creating new breaks in the skin. Allow wounds to bleed freely. Collect urine and feces for signs of internal decontamination.",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Reactive metals (eg, elemental sodium, potassium, lithium)",
"      </td>",
"      <td>",
"       Caustic",
"      </td>",
"      <td>",
"       Apply mineral oil and remove visible particles with forceps, gauze, or towels, then store removed particles in mineral oil.",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    NOTE: The treatment protocols given here are theoretically optimal, but decontamination efforts should not be delayed for a significant time to institute them.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: King C, Henretig FM (Eds). Skin Decontamination. Textbook of Pediatric Emergency Procedures, 2nd ed, Lippincott Williams &amp; Wilkins, 2008.&nbsp;Copyright &copy; 2008 Lippincott Williams &amp; Wilkins.",
"    </div>",
"    <div class=\"contractual\">",
"     <br/>",
"     <a href=\"file://www.lww.com\">",
"      file://www.lww.com",
"     </a>",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
