" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Treatment of herpes zoster in the immunocompetent host",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?32/20/33096/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?32/20/33096/contributors\">",
"     Mary A Albrecht, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?32/20/33096/contributors\">",
"     Section Editor",
"    </a>",
