var title_f32_23_33147="Defin statin-related issues";
var content_f32_23_33147=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PC%2F81799&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PC%2F81799&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Proposed definitions for statin-related issues from muscle expert panel",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"sublist1_start\">",
"       Myopathy",
"      </td>",
"      <td class=\"sublist_other_start\">",
"       General term for all statin-related muscle problems",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Symptomatic myopathy",
"      </td>",
"      <td class=\"sublist_other\">",
"       Signs and symptoms referable to skeletal muscle, including myalgias (muscle pain), weakness (subjective or objective), and cramps",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Asymptomatic myopathy",
"      </td>",
"      <td class=\"sublist_other\">",
"       CK elevations without objective weakness or subjective complaints",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Clinically important rhabdomyolysis",
"      </td>",
"      <td>",
"       Any evidence of muscle cell destruction or enzyme leakage felt to be causally related to a change in renal function",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1_start\" colspan=\"2\">",
"       Levels of CK elevation (whether asymptomatic myopathy or clinically important rhabdomyolysis)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Mild CK increase",
"      </td>",
"      <td class=\"sublist_other\">",
"       CK above the ULN but &lt;10 times ULN",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Moderate CK increase",
"      </td>",
"      <td class=\"sublist_other\">",
"       CK &ge;10 times ULN but &lt;50 times ULN",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"sublist1\">",
"       Marked CK increase",
"      </td>",
"      <td class=\"sublist_other\">",
"       CK &ge;50 times ULN",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     CK: creatine kinase; ULN: upper limit of normal.",
"    </div>",
"    <div class=\"reference\">",
"     Source: Thompson, PD, Clarkson, PM, Rosenson, RS. An Assessment of Statin Safety by Muscle Experts. Am J Cardiol 2006; 97:69C.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
