var title_f32_29_33244="Advantages and disadvantages of treatment in nontoxic MNG";
var content_f32_29_33244=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F78621&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F78621&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Advantages and disadvantages of the treatment options in nontoxic multinodular goiter",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Advantages",
"       </td>",
"       <td class=\"subtitle1\">",
"        Disadvantages",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Surgery",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         Significant goiter reduction",
"        </p>",
"        <p>",
"         Rapid decompression of trachea",
"        </p>",
"        <p>",
"         Prompt relief of symptoms",
"        </p>",
"        <p>",
"         Definite histological diagnosis",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Inpatient",
"        </p>",
"        <p>",
"         High cost",
"        </p>",
"        <p>",
"         Surgical risk",
"        </p>",
"        <p>",
"         Vocal cord paralysis: ~1 percent",
"        </p>",
"        <p>",
"         Hypoparathyroidism: ~1 percent",
"        </p>",
"        <p>",
"         Risk of hypothyroidism dependent of resection",
"        </p>",
"        <p>",
"         Risk of recurrence dependent of resection",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         <sup>",
"          131",
"         </sup>",
"         I",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         Most often outpatient",
"        </p>",
"        <p>",
"         If outpatient: low cost",
"        </p>",
"        <p>",
"         Few subjective side effects",
"        </p>",
"        <p>",
"         Goiter reduction: 50 percent within 1 yr",
"        </p>",
"        <p>",
"         Improves inspiratory capacity in long term",
"        </p>",
"        <p>",
"         Can be repeated successfully",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Limitation of administrated radioactivity",
"        </p>",
"        <p>",
"         Restricted proximity to other persons",
"        </p>",
"        <p>",
"         Contraceptives needed in fertile women",
"        </p>",
"        <p>",
"         Gradual reduction of the goiter",
"        </p>",
"        <p>",
"         Decreasing effect with increasing size",
"        </p>",
"        <p>",
"         Small risk of acute goiter enlargement",
"        </p>",
"        <p>",
"         Risk of thyroiditis: 3 percent",
"        </p>",
"        <p>",
"         Risk of transition into Graves' disease: 5 percent",
"        </p>",
"        <p>",
"         1 yr risk of hypothyroidism: 15 to 20 percent",
"        </p>",
"        <p>",
"         Long-term cancer risk unknown",
"        </p>",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <strong>",
"         <span class=\"small\">",
"          L",
"         </span>",
"         -T",
"         <sub>",
"          4",
"         </sub>",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         Outpatient",
"        </p>",
"        <p>",
"         Low cost",
"        </p>",
"        <p>",
"         May prevent new nodule formation",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Low efficacy",
"        </p>",
"        <p>",
"         Lifelong treatment",
"        </p>",
"        <p>",
"         Adverse effects (bone, heart)",
"        </p>",
"        <p>",
"         Not feasible when TSH is suppressed",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Heged&uuml;s L, Bonnema SJ, Bennedbaek FN. Management of simple nodular goiter: current status and future perspectives. Endocr Rev 2003; 24:102. Copyright &copy; 2003 The Endocrine Society.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
