var title_f32_3_32827="Maternal and fetal benefits of methadone treatment";
var content_f32_3_32827=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F55718&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F55718&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Maternal and fetal benefits of methadone treatment for opioid addiction",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td>",
"       Reduces illegal opiate use as well as the use of other drugs, thus diminishing the risk of hepatitis, HIV/AIDS, and other sexually transmitted diseases",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Helps to remove the opiate-dependent woman from the drug-seeking environment",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Eliminates illegal behaviors, such as prostitution",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Prevents fluctuation of the maternal drug level over the course of the day",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Reduces maternal mortality and severe  morbidity",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Permits a more stable intrauterine environment for the fetus, with a decreased the risk of hypoxia",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Leads to improvement in the mother's nutrition and infant birth weight",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Improves the woman's ability to participate in prenatal care and substance abuse treatment",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Enhances the woman's ability to prepare for the birth of her infant and begin homemaking. Stabilized mothers on methadone are more likely to retain custody of their children.",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Children are more closely monitored when the mother is part of a rehabilitation program",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       Reduces the frequency of obstetrical complications",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: Alcohol and Other Drug Treatment Guidelines for Pregnant, Substance Using Women. In: Treatment Improvement Protocol Series 2: Pregnant, Substance-Using Women, Mitchell, JL (Ed), 1995.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
