var title_f32_3_32828="TNM path staging mel conj";
var content_f32_3_32828=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F73188&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F73188&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=3\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    TNM pathologic staging of melanoma of the conjunctiva",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"2\">",
"       Primary tumor (pT)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       pTX",
"      </td>",
"      <td>",
"       Primary tumor cannot be assessed",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       pT0",
"      </td>",
"      <td>",
"       No evidence of primary tumor",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       pT(is)",
"      </td>",
"      <td>",
"       Melanoma of the conjunctiva confined to the epithelium*",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       pT1a",
"      </td>",
"      <td>",
"       Melanoma of the bulbar conjunctiva not more than 0.5 mm in thickness with invasion of the substantia propria",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       pT1b",
"      </td>",
"      <td>",
"       Melanoma of the bulbar conjunctiva more than 0.5 mm but not more than 1.5 mm in thickness with invasion of the substantia propria",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       pT1c",
"      </td>",
"      <td>",
"       Melanoma of the bulbar conjunctiva greater than 1.5 mm in thickness with invasion of the substantia propria",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       pT2a",
"      </td>",
"      <td>",
"       Melanoma of the palpebral, forniceal, or caruncular conjunctiva not more than 0.5 mm in thickness with invasion of the substantia propria",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       pT2b",
"      </td>",
"      <td>",
"       Melanoma more than 0.5 but not greater than 1.5 mm in thickness with invasion of the substantia propria",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       pT2c",
"      </td>",
"      <td>",
"       Melanoma of the palpebral, forniceal, or caruncular conjunctiva greater than 1.5 mm in thickness with invasion of the substantia propria",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       pT3",
"      </td>",
"      <td>",
"       Melanoma invades the eye, eyelid, nasolacrimal system, sinuses, or orbit",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       pT4",
"      </td>",
"      <td>",
"       Melanoma invades the central nervous system",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"2\">",
"       Regional lymph nodes (pN)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       pNX",
"      </td>",
"      <td>",
"       Regional lymph nodes cannot be assessed",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       pN0",
"      </td>",
"      <td>",
"       No regional lymph node metastasis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       pN1",
"      </td>",
"      <td>",
"       Regional lymph node metastasis present",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\" colspan=\"2\">",
"       Distant metastasis (pM)",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       cM0",
"      </td>",
"      <td>",
"       No distant metastasis",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       pM1",
"      </td>",
"      <td>",
"       Distant metastasis",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * pT(is) melanoma in situ (includes the term primary acquired melanosis) with atypia replacing greater than 75 percent of the normal epithelial thickness, with cytologic features of epithelioid cells, including abundant cytoplasm, vesicular nuclei or prominent nucleoli, and/or presence of intraepithelial nests of atypical cells.",
"    </div>",
"    <div class=\"reference\">",
"     Used with the permission of the American Joint Committee on Cancer (AJCC), Chicago, Illinois. The original source for this material is the AJCC Cancer Staging Manual, Seventh Edition (2010) published by Springer New York, Inc.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
