var title_f32_30_33259="General approach to treatment of fatigue in palliative care";
var content_f32_30_33259=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PALC%2F86231&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PALC%2F86231&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    General approach to treatment of fatigue in palliative care",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Treatment of the underlying cause",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Anemia - Red blood cell transfusion or an erythropoiesis stimulating agent, most useful in patients receiving chemotherapy who have severe anemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Deconditioning - exercise",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Depression - antidepressants",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Infections - antibiotics",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Dehydration - intravenous or subcutaneous fluids",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hypoxia - oxygen,&nbsp;if hypoxemic (eg,&nbsp;oxygen saturation &lt;90 percent by pulse oximetry)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Metabolic and endocrine disorders - correction of disorder (eg, thyroid or testosterone replacement)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Insomnia - sleep hygiene, brief trial of short-acting hypnotics in refractory cases",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Pain - NSAIDs, opioids",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Treatment of the symptom",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Established therapies - glucocorticoids, megestrol acetate",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Less well established therapies - methylphenidate, modafinil, American Ginseng",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Alternative therapies - melatonin, fish oil, yoga, and meditation",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
