var title_f32_30_33260="Irritant and allergic contact dermatitis";
var content_f32_30_33260=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=DERM%2F65351&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=DERM%2F65351&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Characteristics of irritant and allergic contact dermatitis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Irritant contact dermatitis",
"       </td>",
"       <td class=\"subtitle1\">",
"        Allergic contact dermatitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Etiology",
"        </strong>",
"       </td>",
"       <td>",
"        Irritant substances",
"       </td>",
"       <td>",
"        Allergens",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Mechanisms",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         Non-immunological",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Delayed-type hypersensitivity",
"        </p>",
"        <p>",
"         Antigen-specific T cells",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Sites",
"        </strong>",
"       </td>",
"       <td>",
"        Localized to the contact areas",
"       </td>",
"       <td>",
"        <p>",
"         At the site of the contact",
"        </p>",
"        <p>",
"         May be widespread",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Symptoms",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         Burning, stinging, and pain",
"        </p>",
"        <p>",
"         Pruritus may be present",
"        </p>",
"       </td>",
"       <td>",
"        Pruritus",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Onset",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         May appear after first contact",
"        </p>",
"        <p>",
"         Usually minutes to hours (strong irritant) or 24 hours to one week (weak irritant)",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Sensitization phase before onset",
"        </p>",
"        <p>",
"         Usually 24-72 hours after exposure",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Decrescendo phenomenon",
"        </strong>",
"       </td>",
"       <td>",
"        Yes",
"       </td>",
"       <td>",
"        No",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\" colspan=\"3\">",
"        <strong>",
"         Clinical manifestation",
"        </strong>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Acute stage",
"       </td>",
"       <td class=\"sublist_other\">",
"        <p>",
"         Erythema, edema, vesicles, and bullae",
"        </p>",
"        <p>",
"         Necrosis and ulceration in severe cases",
"        </p>",
"       </td>",
"       <td class=\"sublist_other\">",
"        Erythema, edema, vesicles",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Chronic stage",
"       </td>",
"       <td class=\"sublist_other\">",
"        Fissuring, hyperkeratosis, and scaling",
"       </td>",
"       <td class=\"sublist_other\">",
"        Fissuring, hyperkeratosis, and scaling",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Histopathology",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         Mild spongiosis, necrosis of epidermal keratinocyte",
"        </p>",
"       </td>",
"       <td>",
"        Spongiosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Patch testing",
"        </strong>",
"       </td>",
"       <td>",
"        Negative",
"       </td>",
"       <td>",
"        Positive",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
