var title_f32_31_33277="Calcium channel blocker poisoning - Rapid overview";
var content_f32_31_33277=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=EM%2F63193&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=EM%2F63193&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Calcium channel blocker poisoning: Rapid overview",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        <span style=\"color: #ff0000;\">",
"         To obtain emergent consultation with a medical toxicologist, call the United States Poison Control Network at 1-800-222-1222, or access the World Health Organization's list of international poison centers (",
"        </span>",
"        <a href=\"file://www.who.int/gho/phe/chemical_safety/poisons_centres/en/index.html\" target=\"_blank\">",
"         www.who.int/gho/phe/chemical_safety/poisons_centres/en/index.html",
"        </a>",
"        <span style=\"color: #ff0000;\">",
"         ).",
"        </span>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Clinical features",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hypotension and bradycardia are prominent",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Electrocardiogram may show PR prolongation or any bradydysrhythmia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Patients with a significant calcium channel blocker (CCB) ingestion can deteriorate rapidly",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Diagnostic evaluation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Assays for CCBs are not routinely available",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Diagnosis depends on the history and clinical presentation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Differential diagnosis for unexplained bradycardia includes toxicity from medications including CCBs, beta-blockers, clonidine, and digoxin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Structural cardiac disease and active myocardial ischemia must be excluded",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Treatment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Assess and stabilize airway, breathing, and circulation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        For patients with severe calcium channel blocker (CCB) poisoning (eg, profound hypotension refractory to crystalloid boluses and atropine), give ALL of the following: calcium salts, glucagon, high dose insulin and dextrose, vasopressor, and lipid emulsion therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        For patients with mild CCB poisoning, give the following treatments in succession based upon patient response (see topic text for details): IV crystalloid, atropine, calcium salts, glucagon, high dose insulin and dextrose, vasopressor, and lipid emulsion therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Initiate treatment for hypotension and bradycardia, if present:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Intravenous crystalloid (hypotension): isotonic saline 500 to 1000 mL boluses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Atropine (bradycardia): 1 mg IV; may repeat for 3 total doses",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Intravenous calcium (hypotension and/or bradycardia)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\">",
"        Bolus therapy:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Calcium chloride - 10 to 20 mL of 10 percent solution (via central venous access if possible)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Calcium gluconate - 30 to 60 mL of 10 percent solution",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Continuous infusion of 0.5 meq calcium/kg per hour",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Monitor serum calcium and ECG for evidence of hypercalcemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Glucagon (bradycardia)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\">",
"        Bolus therapy:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        1-5 mg IV push, may repeat up to 15 mg total",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\">",
"        Continuous infusion:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Determine bolus amount needed to obtain response; give this \"response dose\" every hour as continuous infusion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Vasopressor support (hypotension)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Norepinephrine: begin 2 mcg/minute IV, titrate rapidly to systolic blood pressure 100 mmHg",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Hyperinsulinemia with euglycemia (hypotension)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\">",
"        Bolus therapy:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Regular insulin 1 Unit/kg IV",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Dextrose 25-50 grams IV; repeat for hypoglycemia; give potassium for hypokalemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\">",
"        Maintenance infusions:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Regular insulin: start infusion at 0.5 Units/kg per hour IV; titrate upwards until hypotension corrected or maximum dose of 2 Units/kg per hour reached",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        Dextrose 0.5 grams/kg per hour; titrate to euglycemia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Gastrointestinal decontamination",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Activated charcoal: 1 gram/kg up to 50 grams",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Whole bowel irrigation: 2 L/hour by mouth; for extended-release preparations only",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_single\">",
"        Consider the following therapies if the above measures fail:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Transvenous cardiac pacing",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Intraaortic balloon pump",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cardiopulmonary bypass",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Extracorporeal membrane oxygenation",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
