var title_f32_32_33292="HIV medications affecting pituitary or adrenal function";
var content_f32_32_33292=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F83250&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F83250&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=7\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Medications used in the treatment of HIV or its complications that may affect pituitary or adrenal gland function",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Medication",
"       </td>",
"       <td class=\"subtitle1\">",
"        Intended clinical use",
"       </td>",
"       <td class=\"subtitle1\">",
"        Endocrine dysfunction",
"       </td>",
"       <td class=\"subtitle1\">",
"        Mechanism of action",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Corticosteroids",
"       </td>",
"       <td>",
"        Anti-inflammatory (eg, adjunctive use in",
"        <em>",
"         Pneumocystis carinii",
"        </em>",
"        pneumonia)",
"       </td>",
"       <td>",
"        Exogenous Cushing's syndrome; glucocorticoid deficiency upon discontinuation; hypogonadism",
"       </td>",
"       <td>",
"        Suppresses hypothalamic-pituitary-adrenal (HPA) axis and hypothalamic-pituitary-gonadal (HPG) axes",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Interferon-&alpha; (IFN-&alpha;)",
"       </td>",
"       <td>",
"        Treatment of co-infection with Hepatitis C",
"       </td>",
"       <td>",
"        Panhypopituarism",
"       </td>",
"       <td>",
"        Immune-mediated",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Ketoconazole",
"       </td>",
"       <td>",
"        Anti-fungal",
"       </td>",
"       <td>",
"        Adrenal insufficiency; hypogonadism",
"       </td>",
"       <td>",
"        Impairs steroid hormone synthesis",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Megestrol acetate",
"       </td>",
"       <td>",
"        Appetite stimulant",
"       </td>",
"       <td>",
"        Cushing's syndrome (rarely); hyperglycemia; hypogonadism; glucocorticoid deficiency upon discontinuation",
"       </td>",
"       <td>",
"        Intrinsic glucocorticoid-like and progestational activity",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Opiates",
"       </td>",
"       <td>",
"        Analgesic",
"       </td>",
"       <td>",
"        Impaired cortisol response to ACTH stimulation; hypogonadism",
"       </td>",
"       <td>",
"        Alterations in HPA &amp; HPG axes",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Protease inhibitors (PIs)",
"       </td>",
"       <td>",
"        Anti-retroviral",
"       </td>",
"       <td>",
"        Hyperprolactinemia",
"       </td>",
"       <td>",
"        May stimulate lactotrophs directly or inhibit metabolism of other medications",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Ritonavir",
"       </td>",
"       <td>",
"        Anti-retroviral",
"       </td>",
"       <td>",
"        Exogenous Cushing's syndrome (with concomitant use of inhaled fluticasone)",
"       </td>",
"       <td>",
"        Effects on hepatic cytochromes and drug metabolism",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Rifampin",
"       </td>",
"       <td>",
"        Antibiotic and anti-mycobacterial agent (eg, tuberculosis)",
"       </td>",
"       <td>",
"        Adrenal insufficiency",
"       </td>",
"       <td>",
"        Increases metabolic clearance of hormones",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Sulfonamides",
"       </td>",
"       <td>",
"        Antibiotic",
"       </td>",
"       <td>",
"        Hyporeninemic hypoaldosteronism",
"       </td>",
"       <td>",
"        Interstitial nephritis",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Courtesy of Melissa Weinberg, MD and Morris Schambelan, MD.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
