var title_f32_36_33358="Evaluation of gonadotropin-dependent precocious puberty";
var content_f32_36_33358=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F52565%7EPEDS%2F59268%7EPEDS%2F72054&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F52565%7EPEDS%2F59268%7EPEDS%2F72054&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Evaluation of gonadotropin-dependent (\"central\") precocious puberty",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"6\" width=\"16%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Etiology",
"       </td>",
"       <td class=\"subtitle1\">",
"        Gonadotropin levels",
"       </td>",
"       <td class=\"subtitle1\">",
"        LH response after GnRH stimulation",
"       </td>",
"       <td class=\"subtitle1\">",
"        Sex steroid hormone levels",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical features",
"       </td>",
"       <td class=\"subtitle1\">",
"        Additional evaluation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         Idiopathic",
"        </p>",
"        <p>",
"         <em>",
"          (80 percent of girls with GDPP)",
"         </em>",
"        </p>",
"        <p>",
"         &nbsp;",
"        </p>",
"        <p>",
"         or CNS tumor",
"        </p>",
"       </td>",
"       <td>",
"        Pubertal levels, with prominent LH pulses during sleep: LH &gt;0.6 IU/L",
"       </td>",
"       <td>",
"        Pubertal response: LH level post GnRH &gt;7 IU/L",
"       </td>",
"       <td>",
"        Pubertal values of estradiol (&gt;9 pg/mL), testosterone (20-1200 ng/dL), and DHEAS",
"       </td>",
"       <td>",
"        <p>",
"         Pubertal development accelerated, but proceeds in normal timing and sequence",
"        </p>",
"        <p>",
"         Enlargement of ovary/uterus (by ultrasound) or testes (by exam)",
"        </p>",
"        <p>",
"         Bone age &gt;height age &gt;chronological age",
"        </p>",
"        <p>",
"         Responsive to GnRH agonist therapy",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Contrast-enhanced MRI of brain to rule out CNS abnormality",
"        </p>",
"        <p>",
"         In boys, measure hCG to exclude an hCG-secreting tumor",
"        </p>",
"        <p>",
"         Skin examination and skeletal survey to rule out McCune-Albright syndrome",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     LH: luteinizing hormone; GnRH: gonadotropin-releasing hormone; CNS: central nervous system; DHEAS: dehydroepiandrosterone sulfate; MRI: magnetic resonance imaging; hCG: human chorionic gonadotropin.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Evaluation of gonadotropin-independent (\"peripheral\") precocious puberty",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"6\" width=\"16%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Etiology",
"       </td>",
"       <td class=\"subtitle1\">",
"        Gonadotropin levels",
"       </td>",
"       <td class=\"subtitle1\">",
"        LH response after GnRH stimulation",
"       </td>",
"       <td class=\"subtitle1\">",
"        Sex steroid hormone levels",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical features",
"       </td>",
"       <td class=\"subtitle1\">",
"        Additional evaluation",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <p>",
"         Congenital adrenal hyperplasia",
"        </p>",
"        (untreated)",
"       </td>",
"       <td>",
"        Prepubertal",
"       </td>",
"       <td>",
"        Absent LH response",
"       </td>",
"       <td>",
"        Sex hormone levels vary depending on the adrenal enzyme block",
"       </td>",
"       <td>",
"        Boys have prepubertal testes with enlarged phallus and pubic hair development. Girls with \"nonclassic\" CAH may present with early pubic and/or axillary hair, and other signs of androgen excess.",
"       </td>",
"       <td>",
"        After therapy with glucocorticoids, GDPP may develop and require GnRH agonist therapy",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <p>",
"         Leydig cell tumor",
"        </p>",
"        (boys)",
"       </td>",
"       <td>",
"        Suppressed",
"       </td>",
"       <td>",
"        Absent LH response",
"       </td>",
"       <td>",
"        Very high levels of testosterone",
"       </td>",
"       <td>",
"        Irregular and asymmetrical enlargement of testes",
"       </td>",
"       <td>",
"        Tumor may not be palpable; testicular ultrasound aids in diagnosis",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Ovarian cyst",
"       </td>",
"       <td>",
"        Suppressed",
"       </td>",
"       <td>",
"        Absent LH response",
"       </td>",
"       <td>",
"        Normal or elevated estradiol; occasionally elevated androgens",
"       </td>",
"       <td>",
"        Pubertal development accelerated; occasionally presents with ovarian torsion and abdominal pain",
"       </td>",
"       <td>",
"        Abdominal ultrasound and/or MRI",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <p>",
"         hCG-secreting germ cell tumors",
"        </p>",
"        (boys)",
"       </td>",
"       <td>",
"        High hCG or positive pregnancy test",
"       </td>",
"       <td>",
"        Absent LH response",
"       </td>",
"       <td>",
"        Pubertal values of testosterone",
"       </td>",
"       <td>",
"        <p>",
"         Testes symmetric and &gt;2.5 cm diameter (&gt;4 mL volume), but smaller than expected for the degree of pubertal development",
"        </p>",
"        Precocious puberty is seen only in boys as hCG only activates LH receptors (estrogen biosynthesis in the ovaries requires both FSH and LH receptor activation)",
"       </td>",
"       <td>",
"        <p>",
"         These tumors may occur in gonads, brain, liver, retroperitoneum, or mediastinum",
"        </p>",
"        When in anterior mediastinum, a karyotype must be performed because of a strong association of this finding with Klinefelter syndrome",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <p>",
"         Familial male-limited (gonadotropin-independent) precocious puberty",
"        </p>",
"        (boys)",
"       </td>",
"       <td>",
"        Suppressed",
"       </td>",
"       <td>",
"        Absent LH response",
"       </td>",
"       <td>",
"        Pubertal values of testosterone",
"       </td>",
"       <td>",
"        <p>",
"         Testes symmetric and &gt;2.5 cm diameter (&gt;4 mL volume), but smaller than expected for degree of pubertal development; spermatogenesis may occur",
"        </p>",
"        <p>",
"         Familial: Male-limited autosomal dominant trait",
"        </p>",
"        Precocious puberty is seen only in boys as there is only activation of the LH receptors, and ovarian estrogen biosynthesis requires both FSH and LH receptor activation",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        <p>",
"         McCune-Albright syndrome",
"        </p>",
"        (girls&gt;&gt;boys)",
"       </td>",
"       <td>",
"        Suppressed",
"       </td>",
"       <td>",
"        Absent LH response",
"       </td>",
"       <td>",
"        Pubertal values of estradiol in girls and testosterone in boys",
"       </td>",
"       <td>",
"        <p>",
"         Ovaries: Enlarged for age and usually containing multiple large cysts",
"        </p>",
"        <p>",
"         Testes: Symmetric and &gt;2.5 cm diameter (&gt;4 mL volume), but smaller than expected for pubertal development",
"        </p>",
"        <p>",
"         Skin: Multiple irregular-edged caf&eacute;-au-lait spots",
"        </p>",
"        Bone: Polyostotic fibrous dysplasia",
"       </td>",
"       <td>",
"        <p>",
"         Ultrasound: Ovaries enlarged, with follicular cysts",
"        </p>",
"        May have other hyperactive endocrine disorders: ie, thyrotoxicosis and/or gigantism",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Virilizing adrenal tumor",
"       </td>",
"       <td>",
"        Suppressed",
"       </td>",
"       <td>",
"        Absent LH response",
"       </td>",
"       <td>",
"        High DHEA or DHEAS*; high androstenedione and testosterone",
"       </td>",
"       <td>",
"        Testes prepubertal",
"       </td>",
"       <td>",
"        CT, MRI, or ultrasound of adrenal glands to locate tumor; if still undetectable selective venous sampling of androgen",
"       </td>",
"      </tr>",
"      <tr class=\"divider_bottom\">",
"       <td>",
"        Adrenal cancer",
"       </td>",
"       <td>",
"        Suppressed",
"       </td>",
"       <td>",
"        Absent LH response",
"       </td>",
"       <td>",
"        High DHEA or DHEAS*; high 17-ketosteroids in urine",
"       </td>",
"       <td>",
"        May present with signs of glucocorticoid excess, and may be associated with hereditary cancer syndromes",
"       </td>",
"       <td>",
"        CT, MRI, or ultrasound of adrenal glands to locate tumor; if still undetectable, selective venous sampling of androgen",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Exogenous sex steroids (eg, testosterone creams and estradiol spray or creams)",
"       </td>",
"       <td>",
"        Suppressed",
"       </td>",
"       <td>",
"        Absent LH response",
"       </td>",
"       <td>",
"        Elevated testosterone in boys and estradiol in girls",
"       </td>",
"       <td>",
"        Estrogen preparations cause feminization, while topical androgens cause virilization in both sexes",
"       </td>",
"       <td>",
"        Clinical history explores use of exogenous sex steroids by caretakers and folk remedies. Monitor sex steroid hormone levels and clinical status after removal of inciting agent.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     GDPP: gonadotropin-dependent precocious puberty; LH: luteinizing hormone; GnRH: gonadotropin-releasing hormone; CAH: congenital adrenal hyperplasia; hCG: human chorionic gonadotropin; FSH: follicle-stimulating hormone; DHEAS: dehydroepiandrosterone sulfate; CT: computed tomography; MRI: magnetic resonance imaging; 17-OHP: 17-hydroxyprogesterone.",
"     <br/>",
"     * If adrenal tumor tissue loses sulfokinase activity, DHEAS may not be elevated.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Evaluation of incomplete precocious puberty",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Etiology",
"       </td>",
"       <td class=\"subtitle1\">",
"        Gonadotropin levels",
"       </td>",
"       <td class=\"subtitle1\">",
"        LH response after GnRH stimulation",
"       </td>",
"       <td class=\"subtitle1\">",
"        Sex steroid hormone levels",
"       </td>",
"       <td class=\"subtitle1\">",
"        Clinical features",
"       </td>",
"       <td class=\"subtitle1\">",
"        Additional evaluation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Premature adrenarche",
"       </td>",
"       <td>",
"        Prepubertal LH levels",
"       </td>",
"       <td>",
"        No LH response",
"       </td>",
"       <td>",
"        <p>",
"         DHEAS values elevated for age (at Tanner 2 levels, ie, &gt;50 micrograms/dL)",
"        </p>",
"        <p>",
"         17-OHP and testosterone normal",
"        </p>",
"        <p>",
"         Early pubertal response to ACTH",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         No other signs of pubertal development; normal growth rate",
"        </p>",
"        <p>",
"         Gonads prepubertal",
"        </p>",
"        <p>",
"         Onset usually after six years of age",
"        </p>",
"        <p>",
"         Associated more frequently with brain injury, obesity, or children born SGA",
"        </p>",
"       </td>",
"       <td>",
"        Monitor for possible early progression to full puberty",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        Premature thelarche",
"       </td>",
"       <td>",
"        Prepubertal LH levels",
"       </td>",
"       <td>",
"        No LH response, normal FSH response",
"       </td>",
"       <td>",
"        Prepubertal",
"       </td>",
"       <td>",
"        No other signs of pubertal development; normal growth rate",
"       </td>",
"       <td>",
"        Monitor for possible early progression to full puberty",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     LH: luteinizing hormone; GnRH: gonadotropin-releasing hormone; DHEAS: dehydroepiandrosterone sulfate; 17-OHP: 17-hydroxyprogesterone; ACTH: adrenocorticotropic hormone; SGA: small for gestational age; FSH: follicle-stimulating hormone.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
