var title_f32_40_33420="Diagnosis early MF";
var content_f32_40_33420=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=HEME%2F74457&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=HEME%2F74457&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Diagnosis of early Mycosis Fungoides",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Criteria",
"       </td>",
"       <td class=\"subtitle1\">",
"        Scoring system",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Clinical",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Basic",
"       </td>",
"       <td>",
"        2 points for basic criteria and two additional criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Persistent and/or progressive patches/thin plaques",
"       </td>",
"       <td>",
"        1 point for basic criteria and one additional criterion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\">",
"        <em>",
"         Additional",
"        </em>",
"        :",
"       </td>",
"       <td rowspan=\"4\">",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        1. Non-sun exposed location",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        2. Size/shape variation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        3. Poikiloderma",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Histopathologic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Basic",
"       </td>",
"       <td>",
"        2 points for basic criteria and two additional criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Superficial lymphoid infiltrate",
"       </td>",
"       <td>",
"        1 point for basic criteria and one additional criterion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2_start\">",
"        <em>",
"         Additional",
"        </em>",
"        :",
"       </td>",
"       <td rowspan=\"3\">",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        1. Epidermotropism without spongiosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist2\">",
"        2. Lymphoid atypia&bull;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Molecular biological",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        1. Clonal&nbsp;T-cell receptor&nbsp;gene rearrangement",
"       </td>",
"       <td>",
"        1 point for clonality",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Immunopathologic",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        1. &lt;50 percent CD2+, CD3+, and/or CD5+ T-cells",
"       </td>",
"       <td rowspan=\"3\">",
"        1 point for one or more criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        2. &lt;10 percent CD7+ T cells",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        3. Epidermal/dermal discordance of CD2, CD3, CD5, or CD7&Delta;",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     A total of 4 points is required for the diagnosis of&nbsp;mycosis fungoides&nbsp;based on any combination of points from the clinical, histopathologic, molecular biological, and immunopathologic criteria.",
"     <br>",
"      &bull; Lymphoid atypia is defined as cells with enlarged hyperchromatic nuclei and irregular or cerebriform nuclear contours.",
"      <br>",
"       &Delta; T-cell antigen deficiency confined to the epidermis.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Pimpinelli, N, Olsen, EA, Santucci, M, et al. Defining early mycosis fungoides. J Am Acad Dermatol 2005; 53:1053. Copyright &copy;2005 Elsevier.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
