var title_f32_41_33435="Genetics myotonic dystrophy";
var content_f32_41_33435=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F65850&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F65850&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Genetic characteristics, prevalence and onset of myotonic dystrophy (DM), types I and II",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1\">",
"       Type of DM",
"      </td>",
"      <td class=\"subtitle1\">",
"       Inheritance and chromosome location",
"      </td>",
"      <td class=\"subtitle1\">",
"       Mutation",
"      </td>",
"      <td class=\"subtitle1\">",
"       Anticipation",
"      </td>",
"      <td class=\"subtitle1\">",
"       Congenital form",
"      </td>",
"      <td class=\"subtitle1\">",
"       Prevalence* and age of onset&bull;",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       DM I (Steinert's disease)",
"      </td>",
"      <td>",
"       AD, 19q13.3",
"      </td>",
"      <td>",
"       Unstable expansion of CTG repeat in DMPK gene",
"      </td>",
"      <td>",
"       Yes",
"      </td>",
"      <td>",
"       Yes, severe developmental delay associated with CTG expansion &gt;750 repeats; affected parent is almost always the mother",
"      </td>",
"      <td>",
"       <p>",
"        1:8000*",
"       </p>",
"       <p>",
"        Congenital&bull;",
"       </p>",
"       <p>",
"        Most common: 20 to 40 years&bull;",
"       </p>",
"       <p>",
"        Occasional: Juvenile onset&bull;",
"       </p>",
"       <p>",
"        Occasional: &gt;40 years&bull;",
"       </p>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td>",
"       DM II (Proximal myotonic myopathy)",
"      </td>",
"      <td>",
"       AD, 3q13.3-q24",
"      </td>",
"      <td>",
"       Expansion of CCTG repeat in intron 1 of ZNF9 gene",
"      </td>",
"      <td>",
"       No strong evidence",
"      </td>",
"      <td>",
"       No",
"      </td>",
"      <td>",
"       <p>",
"        1:8000*",
"       </p>",
"       <p>",
"        Most common: 30 to 60 years&bull;",
"       </p>",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     AD: autosomal dominant.",
"     <br>",
"      * Proportions of DM caused by types I and II is not yet known.",
"      <br>",
"       &bull; Age of onset.",
"      </br>",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
