var title_f32_42_33452="Immune Rx systemic sclerosis";
var content_f32_42_33452=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=RHEUM%2F79035&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=RHEUM%2F79035&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Immunosuppressive therapies for systemic sclerosis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Treatment",
"       </td>",
"       <td class=\"subtitle1\">",
"        Mechanism of action",
"       </td>",
"       <td class=\"subtitle1\">",
"        Comments",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Selective immunosuppression",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cyclosporine",
"       </td>",
"       <td>",
"        Inhibits T-helper cell actions by reducing IL-2 release, may reduce collagen synthesis",
"       </td>",
"       <td>",
"        Reported beneficial effects on skin sclerosis may be confounded by increased incidence of renal crisis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Antithymocyte globulin",
"       </td>",
"       <td rowspan=\"2\">",
"        Temporary suppression of cell mediated immunity",
"       </td>",
"       <td rowspan=\"2\">",
"        Possible benefit for skin sclerosis although controlled trial of ATG confirms considerable therapy associated morbidity",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Antilymphocyte globulin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        CAMPATH-1H",
"       </td>",
"       <td>",
"        Lympholytic humanized monoclonal antibody",
"       </td>",
"       <td>",
"        Anecdotal benefit in case reports",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Photopheresis",
"       </td>",
"       <td>",
"        Extracorporeal photoactivated 8-methoxypsoralen inhibits activated T-cells",
"       </td>",
"       <td>",
"        Benefit has been reported but good placebo-controlled clinical trial is needed",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Plasmapheresis",
"       </td>",
"       <td>",
"        Removes circulating immune mediators",
"       </td>",
"       <td>",
"        Equivocal results but anecdotal benefit",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Anti-CD20",
"       </td>",
"       <td>",
"        Depletion of B lymphocytes",
"       </td>",
"       <td>",
"        Little evidence for skin improvement; some cases suggest improvement of lung fibrosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Nonselective immunosuppression",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Methotrexate",
"       </td>",
"       <td>",
"        Folic acid antagonist",
"       </td>",
"       <td>",
"        Two trials suggest potential benefit for skin disease",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Cyclophosphamide",
"       </td>",
"       <td>",
"        Alkylating agent",
"       </td>",
"       <td>",
"        Evidence of benefit for lung fibrosis in randomized trials and open-label studies; cyclophosphamide often combined with glucocorticoids",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Azathioprine",
"       </td>",
"       <td>",
"        Antimetabolite",
"       </td>",
"       <td>",
"        Anecdotal benefit reported in open studies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Autologous stem cell transplantation",
"       </td>",
"       <td>",
"        High dose immunosuppression (often with cyclophosphamide) followed by stem cell transplantation",
"       </td>",
"       <td>",
"        Limited evidence suggesting benefit for skin and on functional assessment; awaiting results from ongoing large randomized trials",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
