" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Treatment of Beh&ccedil;et&rsquo;s disease",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"UTD.htm?32/45/33498/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"UTD.htm?32/45/33498/contributors\">",
