var title_f32_48_33547="Indications bone density";
var content_f32_48_33547=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ENDO%2F58752&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ENDO%2F58752&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    The National Osteoporosis Foundation indications for bone density testing",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Women aged 65 years and older and men age 70 and older, regardless of clinical risk factors.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Younger postmenopausal women and men age 50 to 70 about whom you have concern based on their clinical risk factor profile.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Women in the menopausal transition if there is a specific risk factor associated with increased fracture risk, such as low body weight, prior low-trauma fracture, or high risk medication.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Adults who have a fracture after age 50.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Adults with a disease or condition (eg, rheumatoid arthritis) associated with low bone mass or bone loss.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Adults taking medication (eg, glucocorticoids, 5 mg or more daily for&nbsp;three or more months) associated with low bone mass or bone loss.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Anyone being considered for pharmacologic osteoporosis therapy.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Anyone being treated for osteoporosis, to monitor treatment effect.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Anyone not receiving therapy in whom evidence of bone loss would lead to treatment.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Postmenopausal women discontinuing estrogen should be considered for bone density testing according to the indications listed above.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: The National Osteoporosis Foundation Clinician's Guide to Prevention and Treatment of Osteoporosis. file://www.nof.org/professionals/NOF_Clinician's_Guide.htm (Accessed April 28, 2008).",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
