var title_f32_48_33548="Risk stratification";
var content_f32_48_33548=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=OBGYN%2F74878&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=11\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=OBGYN%2F74878&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=11\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Risk of death or severe morbidity during pregnancy",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Group",
"       </td>",
"       <td class=\"subtitle1\">",
"        Cardiac disease",
"       </td>",
"       <td class=\"subtitle1\">",
"        Risk of cardiac complications",
"       </td>",
"       <td class=\"subtitle1\">",
"        Mortality",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        I",
"       </td>",
"       <td>",
"        <p>",
"         Small left-right shunt (atrial septal defect, ventricular septal defect, patent ductus arteriosus)",
"        </p>",
"        <p>",
"         Mild pulmonic/tricuspid valve abnormalities",
"        </p>",
"        <p>",
"         Corrected tetralogy of Fallot",
"        </p>",
"        <p>",
"         Bioprosthetic valve",
"        </p>",
"        <p>",
"         Repaired lesions without residual cardiac dysfunction",
"        </p>",
"        <p>",
"         Isolated mitral valve prolapse without significant regurgitation",
"        </p>",
"        <p>",
"         Bicuspid aortic valve without stenosis",
"        </p>",
"        <p>",
"         Valvular regurgitation with normal ventricular systolic function",
"        </p>",
"       </td>",
"       <td>",
"        Low risk",
"       </td>",
"       <td>",
"        &lt;1 percent",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        II",
"       </td>",
"       <td>",
"        <p>",
"         Mitral stenosis",
"        </p>",
"        <p>",
"         Large left-to-right shunt",
"        </p>",
"        <p>",
"         Mechanical prosthetic heart valve",
"        </p>",
"        <p>",
"         Severe pulmonic stenosis",
"        </p>",
"        <p>",
"         Aortic stenosis",
"        </p>",
"        <p>",
"         Uncorrected coarctation of aorta",
"        </p>",
"        <p>",
"         Uncorrected tetralogy of Fallot and other unrepaired cyanotic congenital heart disease",
"        </p>",
"        <p>",
"         Previous myocardial infarction",
"        </p>",
"        <p>",
"         Moderate to severe systemic ventricular dysfunction",
"        </p>",
"        <p>",
"         History of peripartum cardiomyopathy without residual dysfunction",
"        </p>",
"       </td>",
"       <td>",
"        Intermediate risk",
"       </td>",
"       <td>",
"        5-15 percent",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        III",
"       </td>",
"       <td>",
"        <p>",
"         Marfan syndrome with aortic or valvular involvement",
"        </p>",
"        <p>",
"         Severe pulmonary hypertension",
"        </p>",
"        <p>",
"         NYHA class III and IV symptoms",
"        </p>",
"        <p>",
"         Severe aortic stenosis",
"        </p>",
"        <p>",
"         History of peripartum cardiomyopathy with residual ventricular dysfunction",
"        </p>",
"       </td>",
"       <td>",
"        High risk",
"       </td>",
"       <td>",
"        25-50 percent",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    The above table represents a general guideline, but patient-specific factors must be taken into account. For example, some women with repaired Tetralogy of Fallot may have significant pulmonary regurgitation, which increases their risk of cardiac complications. In addition, effective anticoagulation can be difficult to maintain in pregnant women with mechanical prosthetic values, particularly older generation mitral valves, thus placing these women at high risk of bleeding or thrombotic complications.",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: van Mook WN, Peeters L. Severe cardiac disease in pregnancy, part I: hemodynamic changes and complaints during pregnancy, and general management of cardiac disease in pregnancy. Curr Opin Crit Care 2005; 11:430. Copyright &copy; 2005 Lippincott Williams &amp; Wilkins.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
