var title_f32_50_33579="Groups at high risk for influenza complications";
var content_f32_50_33579=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F72029&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F72029&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Groups at high risk for influenza complications",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Children &lt;2 years*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Adults &ge;65 years of age",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Persons with chronic pulmonary (including asthma), cardiovascular (except hypertension), renal, hepatic, hematologic (including sickle cell disease), metabolic (including diabetes mellitus), neurologic, neuromuscular, and neurodevelopmental disorders (including disorders of the brain, spinal cord, peripheral nerve and muscle such as cerebral palsy, epilepsy, stroke, intellectual disability [mental retardation], moderate to severe developmental delay, muscular dystrophy, or spinal cord injury)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Immunosuppression (including immunosuppression caused by medications or by human immunodeficiency virus)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Women who are pregnant or postpartum (within 2 weeks after delivery)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Children &lt;19 years of age and receiving long-term aspirin therapy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Native Americans and Alaskan Natives",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Morbidly obese (body mass index [BMI] &ge;40 for adults or BMI &gt;2.33 standard deviations above the mean for children)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Residents of nursing homes and other chronic care facilities",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * Although all children &lt;5 years of age&nbsp;are considered to be at higher risk for complications of influenza, the highest risk is for those &lt;2 years of age, with the highest hospitalization and death rates among infants &lt;6 months of age.",
"    </div>",
"    <div class=\"reference\">",
"     Adapted from: Centers for Disease Control and Prevention. 2011-2012 Influenza antiviral medications: Summary for clinicians. file://www.cdc.gov/flu/professionals/antivirals/summary-clinicians.htm (Accessed on June 28, 2012).",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
