" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Focal segmental glomerulosclerosis in the transplanted kidney",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?32/59/33722/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?32/59/33722/contributors\">",
"     Daniel C Brennan, MD, FACP",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?32/59/33722/contributors\">",
"     Nada Alachkar, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?32/59/33722/contributors\">",
"     Andre A Kaplan, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?32/59/33722/contributors\">",
"     Christopher H Kwoh, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?32/59/33722/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?32/59/33722/contributors\">",
"     Barbara Murphy, MB, BAO, BCh, FRCPI",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?32/59/33722/contributors\">",
"     Deputy Editor",
