var title_f32_6_32878="2010 revised criteria for diagnosis of ARVC";
var content_f32_6_32878=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F58452&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F58452&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    2010 revised Task Force criteria for the diagnosis of Arrhythmogenic Right Ventricular Cardiomyopathy (ARVC)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        &nbsp;",
"       </td>",
"       <td class=\"subtitle1\">",
"        Revised Task Force criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        I. Global or regional dysfunction and structural alterations*",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"3\">",
"        <strong>",
"         Major",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          By 2D echo:",
"         </strong>",
"        </p>",
"        <p>",
"         &bull; Regional RV akinesia, dyskinesia, or aneurysm",
"        </p>",
"        <p>",
"         &bull;",
"         <em>",
"          and",
"         </em>",
"         1 of the following (end diastole):",
"        </p>",
"        <p>",
"         &nbsp;&nbsp;&nbsp;- PLAX RVOT &ge;32 mm (corrected for body size [PLAX/BSA] &ge;19 mm/m",
"         <sup>",
"          2",
"         </sup>",
"         )",
"        </p>",
"        <p>",
"         &nbsp;&nbsp;&nbsp;- PSAX RVOT &ge;36 mm (corrected for body size [PSAX/BSA] &ge;21 mm/m",
"         <sup>",
"          2",
"         </sup>",
"         )",
"        </p>",
"        <p>",
"         &nbsp;&nbsp;&nbsp;-",
"         <em>",
"          or",
"         </em>",
"         fractional area change &le;33 percent",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         <strong>",
"          By MRI:",
"         </strong>",
"        </p>",
"        <p>",
"         &bull; Regional RV akinesia or dyskinesia or dyssynchronous RV contraction",
"        </p>",
"        <p>",
"         &bull;",
"         <em>",
"          and",
"         </em>",
"         1 of the following:",
"        </p>",
"        <p>",
"         &nbsp;&nbsp;&nbsp;- Ratio of RV end-diastolic volume to BSA &ge;110 mL/m",
"         <sup>",
"          2",
"         </sup>",
"         (male) or &ge;100 mL/m",
"         <sup>",
"          2",
"         </sup>",
"         (female)",
"        </p>",
"        <p>",
"         &nbsp;&nbsp;&nbsp;-",
"         <em>",
"          or",
"         </em>",
"         RV ejection fraction &le;40 percent",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         <strong>",
"          By RV angiography:",
"         </strong>",
"        </p>",
"        <p>",
"         &bull; Regional RV akinesia, dyskinesia, or aneurysm",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td rowspan=\"2\">",
"        <strong>",
"         Minor",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         <strong>",
"          By 2D echo:",
"         </strong>",
"        </p>",
"        <p>",
"         &bull; Regional RV akinesia or dyskinesia",
"        </p>",
"        <p>",
"         &bull;",
"         <em>",
"          and",
"         </em>",
"         1 of the following (end diastole):",
"        </p>",
"        <p>",
"         &nbsp;&nbsp;&nbsp;- PLAX RVOT &ge;29 to &lt;32 mm (corrected for body size [PLAX/BSA] &ge;16 to &lt;19 mm/m",
"         <sup>",
"          2",
"         </sup>",
"         )",
"        </p>",
"        <p>",
"         &nbsp;&nbsp;&nbsp;- PSAX RVOT &ge;32 to &lt;36 mm (corrected for body size [PSAX/BSA] &ge;18 to &lt;21 mm/m",
"         <sup>",
"          2",
"         </sup>",
"         )",
"        </p>",
"        <p>",
"         &nbsp;&nbsp;&nbsp;-",
"         <em>",
"          or",
"         </em>",
"         fractional area change &gt;33 percent to &le;40 percent",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <p>",
"         <strong>",
"          By MRI:",
"         </strong>",
"        </p>",
"        <p>",
"         &bull; Regional RV akinesia or dyskinesia or dyssynchronous RV contraction",
"        </p>",
"        <p>",
"         &bull;",
"         <em>",
"          and",
"         </em>",
"         1 of the following:",
"        </p>",
"        <p>",
"         &nbsp;&nbsp;&nbsp;- Ratio of RV end-diastolic volume to BSA &ge;100 to &lt;110 mL/m",
"         <sup>",
"          2",
"         </sup>",
"         (male) or &ge;90 to &lt;100 mL/m",
"         <sup>",
"          2",
"         </sup>",
"         (female)",
"        </p>",
"        <p>",
"         &nbsp;&nbsp;&nbsp;-",
"         <em>",
"          or",
"         </em>",
"         RV ejection fraction &gt;40 percent to &le;45 percent",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        II. Tissue characterization of wall",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         Major",
"        </strong>",
"       </td>",
"       <td>",
"        &bull; Residual myocytes &lt;60 percent by morphometric analysis (or &lt;50 percent if estimated), with fibrous replacement of the RV free wall myocardium in &ge;1 sample, with or without fatty replacement of tissue on endomyocardial biopsy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         Minor",
"        </strong>",
"       </td>",
"       <td>",
"        &bull; Residual myocytes 60 percent to 75 percent by morphometric analysis (or 50 percent to 65 percent if estimated), with fibrous replacement of the RV free wall myocardium in &ge;1 sample, with or without fatty replacement of tissue on endomyocardial biopsy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        III. Repolarization abnormalities",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         Major",
"        </strong>",
"       </td>",
"       <td>",
"        &bull; Inverted T waves in right precordial leads (V",
"        <sub>",
"         1",
"        </sub>",
"        , V",
"        <sub>",
"         2",
"        </sub>",
"        , and V",
"        <sub>",
"         3",
"        </sub>",
"        ) or beyond in individuals &gt;14 years of age (in the absence of complete right bundle-branch block QRS &ge;120 ms)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         Minor",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         &bull; Inverted T waves in leads V",
"         <sub>",
"          1",
"         </sub>",
"         and V",
"         <sub>",
"          2",
"         </sub>",
"         in individuals &gt;14 years of age (in the absence of complete right bundle-branch block) or in V",
"         <sub>",
"          4",
"         </sub>",
"         , V",
"         <sub>",
"          5",
"         </sub>",
"         , or V",
"         <sub>",
"          6",
"         </sub>",
"        </p>",
"        <p>",
"         &bull; Inverted T waves in leads V",
"         <sub>",
"          1",
"         </sub>",
"         , V",
"         <sub>",
"          2",
"         </sub>",
"         , V",
"         <sub>",
"          3",
"         </sub>",
"         , and V",
"         <sub>",
"          4",
"         </sub>",
"         in individuals &gt;14 years of age in the presence of complete right bundle-branch block",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        IV. Depolarization/conduction abnormalities",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         Major",
"        </strong>",
"       </td>",
"       <td>",
"        &bull; Epsilon wave (reproducible low-amplitude signals between end of QRS complex to onset of the T wave) in the right precordial leads (V",
"        <sub>",
"         1",
"        </sub>",
"        to V",
"        <sub>",
"         3",
"        </sub>",
"        )",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         Minor",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         &bull; Late potentials by SAECG in &ge;1 of the following 3 parameters in the absence of a QRS duration of &ge;110 ms on the standard ECG",
"        </p>",
"        <p>",
"         &nbsp;&nbsp;&nbsp;- Filtered QRS duration (fQRS) &ge;114 ms",
"        </p>",
"        <p>",
"         &nbsp;&nbsp;&nbsp;- Duration of terminal QRS &lt;40 &micro;V (low-amplitude signal duration) &ge;38 ms",
"        </p>",
"        <p>",
"         &nbsp;&nbsp;&nbsp;- Root-mean-square voltage of terminal 40 ms &le;20 &micro;V",
"        </p>",
"        <p>",
"         &bull; Terminal activation duration of QRS &ge;55 ms measured from the nadir of the S wave to the end of the QRS, including R', in V",
"         <sub>",
"          1",
"         </sub>",
"         , V",
"         <sub>",
"          2",
"         </sub>",
"         , or V",
"         <sub>",
"          3",
"         </sub>",
"         , in the absence of complete right bundle-branch block",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        V. Arrhythmias",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         Major",
"        </strong>",
"       </td>",
"       <td>",
"        &bull; Nonsustained or sustained ventricular tachycardia of left bundle-branch morphology with superior axis (negative or indeterminate QRS in leads II, III, and aVF and positive in lead aVL)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         Minor",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         &bull; Nonsustained or sustained ventricular tachycardia of RV outflow configuration, left bundle-branch block morphology with inferior axis (positive QRS in leads II, III, and aVF and negative in lead aVL) or of unknown axis",
"        </p>",
"        <p>",
"         &bull; &gt;500 ventricular extrasystoles per 24 hours (Holter)",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        VI. Family history",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         Major",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         &bull; ARVC/D confirmed in a first-degree relative who meets current Task Force criteria",
"        </p>",
"        <p>",
"         &bull; ARVC/D confirmed pathologically at autopsy or surgery in a first-degree relative",
"        </p>",
"        <p>",
"         &bull; Identification of a pathogenic mutation&Delta; categorized as associated or probably associated with ARVC/D in the patient under evaluation",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        <strong>",
"         Minor",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         &bull; History of ARVC/D in a first-degree relative in whom it is not possible or practical to determine whether the family member meets current Task Force criteria",
"        </p>",
"        <p>",
"         &bull; Premature sudden death (&lt;35 years of age) due to suspected ARVC/D in a first-degree relative",
"        </p>",
"        <p>",
"         &bull; ARVC/D confirmed pathologically or by current Task Force Criteria in second-degree relative",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    Diagnostic terminology for revised criteria:",
"    <br>",
"     <br>",
"      <ul>",
"       <li>",
"        Definite diagnosis: 2 Major,&nbsp;OR 1 Major and 2 Minor criteria, OR 4 Minor from different categories",
"       </li>",
"       <li>",
"        Borderline diagnosis: 1 Major and 1 Minor, OR 3 Minor criteria from different categories",
"       </li>",
"       <li>",
"        Possible diagnosis: 1 Major, OR 2 Minor criteria from different categories",
"       </li>",
"      </ul>",
"      <p>",
"       &nbsp;",
"      </p>",
"      <div class=\"footnotes\">",
"       PLAX: parasternal long-axis view; RVOT: RV outflow tract; BSA: body surface area; PSAX: parasternal short-axis view; aVF: augmented voltage unipolar left foot lead; aVL: augmented voltage unipolar left arm lead.",
"       <br>",
"        * Hypokinesis is not included in this or subsequent definitions of RV regional wall motion abnormalities for the proposed modified criteria.",
"        <br>",
"         &Delta; A pathogenic mutation is a DNA alteration associated with ARVC/D that alters or is expected to alter the encoded protein, is unobserved or rare in a large non-ARVC/D control population, and either alters or is predicted to alter the structure or function of the protein or has demonstrated linkage to the disease phenotype in a conclusive pedigree.",
"        </br>",
"       </br>",
"      </div>",
"      <div class=\"reference\">",
"       Modified with permission from: Marcus FI, McKenna WJ, Sherrill D, et al. Diagnosis of arrythmogenic right ventricular cardiomyopathy/dysplasia: proposed modification of the Task Force criteria. Circulation 2010; 121:1533. Copyright &copy; 2010 Lippincott Williams &amp; Wilkins.",
"      </div>",
"     </br>",
"    </br>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
