" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Clinical manifestations of adrenal insufficiency in adults",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
