" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Treatment of advanced, unresectable gallbladder cancer",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
