" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Clinical criteria of feeding intolerance in preterm infants",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Abdominal examination",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Distension",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Tenderness",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Change in bowel sounds (eg, increased or absent)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Emesis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Gastric residual volume",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Greater than 2 mL/kg of body weight",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Greater than half the volume of feeds over prior&nbsp;three hours",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Any change in quantity of fluid volume (usually increase)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Gastric residual fluid characteristics",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        Change in color",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Green: Bile",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Red or brown: Blood",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Stools",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Any change in frequency",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Presence of blood or guaiac positive stools",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Clinical status",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Any change in clinical status",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
