var title_f33_18_34092="Methods for diagnosing human herpesvirus 6 infections";
var content_f33_18_34092=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ID%2F83219&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ID%2F83219&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=5\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    HHV-6 diagnostic tests",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Assay",
"       </td>",
"       <td class=\"subtitle1\">",
"        Advantages",
"       </td>",
"       <td class=\"subtitle1\">",
"        Disadvantages",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        <strong>",
"         Culture",
"        </strong>",
"       </td>",
"       <td>",
"        <p>",
"         Positive results represent active infection or replication",
"        </p>",
"        <p>",
"         Able to distinguish between HHV-6A and HHV-6B variants",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Technically difficult",
"        </p>",
"        <p>",
"         Long turn-around time",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        PCR",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle3_left\" colspan=\"3\">",
"        DNA PCR",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        Plasma",
"       </td>",
"       <td>",
"        <p>",
"         Correlates well with diagnostic and clinical indicators of primary infection and with virus isolation after transplantation",
"        </p>",
"        <p>",
"         Able to distinguish between HHV-6A and HHV-6B variants",
"        </p>",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        PBMC",
"       </td>",
"       <td>",
"        <p>",
"         Able to distinguish between HHV-6A and HHV-6B variants",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Requires quantitative cut-offs for established sensitivity threshold in order to distinguish latent from active infection",
"        </p>",
"        <p>",
"         Negative results may be difficult to interpret in lymphopenic patients (eg, HCT recipients early after transplantation)",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent2\">",
"        CSF",
"       </td>",
"       <td>",
"        Able to distinguish between HHV-6A and HHV-6B variants",
"       </td>",
"       <td>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle3_left\">",
"        Reverse transcription - PCR",
"       </td>",
"       <td>",
"        <p>",
"         Positive results represent active infection or replication",
"        </p>",
"       </td>",
"       <td>",
"        &nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"3\">",
"        Serology",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle3_left\">",
"        Conventional",
"       </td>",
"       <td>",
"        <p>",
"         &nbsp;",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Does not reliably distinguish between active and latent infection",
"        </p>",
"        <p>",
"         There may be interference from maternal antibodies around the time of primary infection",
"        </p>",
"        <p>",
"         Immunocompromised patients do not reliably mount an antibody response",
"        </p>",
"        <p>",
"         Unable to distinguish between HHV-6A and HHV-6B variants",
"        </p>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle3_left\">",
"        Avidity assays",
"       </td>",
"       <td>",
"        <p>",
"         Able to distinguish between antibody associated with primary versus established infection",
"        </p>",
"       </td>",
"       <td>",
"        <p>",
"         Unable to distinguish between reactivation and latent infection",
"        </p>",
"        <p>",
"         Unable to distinguish between HHV-6A and HHV-6B variants",
"        </p>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     HHV-6: human herpesvirus 6; PCR: polymerase chain reaction; PBMC: peripheral blood mononuclear cells; HCT: hematopoietic cell transplantation; CSF: cerebrospinal fluid.",
"    </div>",
"    <div class=\"reference\">",
"     Courtesy of Danielle Zerr, MD, MPH.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
