"    <li>",
"     <a class=\"sr_button\" href=\"#H7761320\" id=\"summRecButton\">",
"      <span>",
"       SUMMARY",
"      </span>",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#H1\">",
"      INTRODUCTION",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#H2\">",
"      TYPES OF NYSTAGMUS",
"     </a>",
"    </li>",
"    <li class=\"bulletItem\">",
"     <a class=\"outlineLink\" href=\"#H3\">",
"      Jerk nystagmus",
"     </a>",
"    </li>",
"    <li class=\"bulletItem\">",
"     <a class=\"outlineLink\" href=\"#H4\">",
"      Pendular nystagmus",
"     </a>",
"    </li>",
"    <li class=\"plainItem\">",
"     <a class=\"outlineLink\" href=\"#H5\">",
"      BASIC CLINICAL VESTIBULAR PHYSIOLOGY",
"     </a>",
