" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Risk-reducing bilateral salpingo-oophorectomy in women at high risk of epithelial ovarian and fallopian tubal cancer",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?33/25/34202/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?33/25/34202/contributors\">",
"     Michael G Muto, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?33/25/34202/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?33/25/34202/contributors\">",
"     Barbara Goff, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
