" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Treatment of giant cell (temporal) arteritis",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?33/26/34217/contributors\">",
"     Author",
"    </a>",
"    <br/>",
