var title_f33_27_34235="Symptoms and sequelae of date rape";
var content_f33_27_34235=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F56678&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F56678&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Possible symptoms and sequelae of date or acquaintance rape",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td>",
"        Disturbances in sleep (eg, taking more than 20 minutes to fall asleep, nightmares)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decrease in appetite; less interest in eating",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Somatic reactions (eg, chronic pelvic pain, recurrent abdominal pain, chronic headache, multiple physical complaints)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Depressive symptoms",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Suicidality",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Anxiety",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Signs of post-traumatic stress disorder (emotional numbing, diminished interest in everyday activities, detachment from others)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Psychiatric or behavior problems that usually are found in the other gender (eg, eating disorders in boys or fighting in girls)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        School failure",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreased self-esteem, self-worth, and/or confidence in their sense of judgment",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Difficulty maintaining relationships",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Sexual dysfunction",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Alcohol or substance use",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Decreased sexual negotiation skills (may lead to pregnancy or sexually transmitted infections)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        For male victims: Fragility of sense of masculinity and confusion about sexual orientation",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
