" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Transcatheter aortic valve replacement",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?33/29/34264/contributors\">",
"     Authors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?33/29/34264/contributors\">",
"     William H Gaasch, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?33/29/34264/contributors\">",
"     Stephen JD Brecker, MD, FRCP, FESC, FACC",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?33/29/34264/contributors\">",
"     Gabriel S Aldea, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?33/29/34264/contributors\">",
"     Section Editors",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?33/29/34264/contributors\">",
"     Catherine M Otto, MD",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?33/29/34264/contributors\">",
"     Donald Cutlip, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?33/29/34264/contributors\">",
"     Deputy Editor",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?33/29/34264/contributors\">",
"     Susan B Yeon, MD, JD, FACC",
"    </a>",
"    <br/>",
"   </div>",
"  </div>",
"  <div id=\"disclosures\">",
"   <a href=\"mobipreview.htm?33/29/34264/contributor-disclosure\" target=\"_blank\">",
"    Disclosures",
"   </a>",
"  </div>",
"  <div id=\"reviewProcess\">",
"   <span>",
"    All topics are updated as new evidence becomes available and our",
"   </span>",
"   <a href=\"/home/editorial-policy\" target=\"_blank\">",
"    peer review process",
"   </a>",
"   <span>",
"    is complete.",
"   </span>",
"  </div>",
"  <div id=\"literatureReviewDate\">",
"   <span class=\"emphasis\">",
"    Literature review current through:",
"   </span>",
"   Oct 2013.",
"   <span class=\"pipeSpace\">",
"    |",
"   </span>",
