var title_f33_29_34268="Treating hypertension in acute ischemic stroke";
var content_f33_29_34268=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F50725&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F50725&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Approach to arterial hypertension in acute ischemic stroke",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Indication that patient is eligible for treatment with intravenous rtPA or other acute reperfusion intervention",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Blood pressure level",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Systolic &gt;185 mm Hg or diastolic &gt;110 mm Hg",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Labetalol 10 to 20 mg IV over 1 to 2 minutes, may repeat x1;",
"       <strong>",
"        or",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Nitropaste 1 to 2 inches;",
"       <strong>",
"        or",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Nicardipine infusion, 5 mg/h, titrate up by 2.5 mg/h at 5- to 15-minute intervals, maximum dose 15 mg/h; when desired blood pressure attained, reduce to 3 mg/h",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       If blood pressure does not decline and remains &gt;185/110 mm Hg, do not administer rtPA",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle1_single\">",
"       Management of blood pressure during and after treatment with rtPA or other acute reperfusion intervention",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Monitor blood pressure every 15 minutes during treatment and then for another 2 hours, then every 30 minutes for 6 hours, and then every hour for 16 hours",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"subtitle2_single\">",
"       Blood pressure level",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Systolic 180 to 230 mm Hg or diastolic 105 to 120 mm Hg",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Labetalol 10 mg IV over 1 to 2 minutes, may repeat every 10 to 20 minutes, maximum dose of 300 mg;",
"       <strong>",
"        or",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Labetalol 10 mg IV followed by an infusion at 2 to 8 mg/min",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       Systolic &gt;230 mm Hg or diastolic 121 to 140 mm Hg",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Labetalol 10 mg IV over 1 to 2 minutes, may repeat every 10 to 20 minutes, maximum dose of 300 mg;",
"       <strong>",
"        or",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Labetalol 10 mg IV followed by an infusion at 2 to 8 mg/min;",
"       <strong>",
"        or",
"       </strong>",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent2\">",
"       Nicardipine infusion, 5 mg/h, titrate up to desired effect by increasing 2.5 mg/h every 5 minutes to maximum of 15 mg/h",
"      </td>",
"     </tr>",
"     <tr>",
"      <td class=\"indent1\">",
"       If blood pressure not controlled, consider sodium nitroprusside",
"      </td>",
"     </tr>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Adams HP Jr, del Zoppo G, Alberts MJ, et al. Guidelines for the early management of adults with ischemic stroke: a guideline from the American Heart Association/American Stroke Association Stroke Council, Clinical Cardiology Council, Cardiovascular Radiology and Intervention Council, and the Atherosclerotic Peripheral Vascular Disease and Quality of Care Outcomes in Research Interdisciplinary Working Groups: the American Academy of Neurology affirms the value of this guideline as an educational tool for neurologists. Stroke 2007; 38:1655. Copyright &copy; 2007 Lippincott Williams &amp; Wilkins.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
