var title_f33_30_34283="NCI CTCAE v40 grading for thromboembolism";
var content_f33_30_34283=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=ONC%2F87383&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=ONC%2F87383&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    National Cancer Institute Common Terminology Criteria for a thromboembolic event* (CTCAE v 4.0)",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup width=\"10%\">",
"     </colgroup>",
"     <colgroup width=\"90%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Grade",
"       </td>",
"       <td class=\"subtitle1\">",
"        Description",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        1",
"       </td>",
"       <td>",
"        Venous thrombosis (eg, superficial thrombosis)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        2",
"       </td>",
"       <td>",
"        Venous thrombosis (eg, uncomplicated deep vein thrombosis), medical intervention indicated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        3",
"       </td>",
"       <td>",
"        Thrombosis (eg, uncomplicated pulmonary embolism [venous], nonembolic cardiac mural [arterial] thrombus), medical intervention indicated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        4",
"       </td>",
"       <td>",
"        Life-threatening (eg, pulmonary embolism, cerebrovascular event, arterial insufficiency); hemodynamic or neurologic instability; urgent intervention indicated",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"centered\">",
"        5",
"       </td>",
"       <td>",
"        Death",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     * A disorder characterized by occlusion of a vessel by a thrombus that has migrated from a distal site via the blood stream.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced from: Common Terminology Criteria for Adverse Events, version 4.0, June 2010, National Institutes of Health, National Cancer Institute. Available at:",
"     <a href=\"file://evs.nci.nih.gov/ftp1/CTCAE/CTCAE_4.03_2010-06-14_QuickReference_5x7.pdf\" target=\"_blank\">",
"      file://evs.nci.nih.gov/ftp1/CTCAE/CTCAE_4.03_2010-06-14_QuickReference_5x7.pdf",
"     </a>",
"     (Accessed December 17, 2012).",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
