var title_f33_34_34347="Diagnostic criteria for diffuse panbronchiolitis";
var content_f33_34_34347=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PULM%2F86536&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PULM%2F86536&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=6\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Diagnostic criteria for diffuse panbronchiolitis*",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Noninvasive",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Persistent cough, sputum, and exertional dyspnea",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        History or current symptoms of chronic sinusitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Bilateral, diffuse, small nodular shadows on a plain chest radiograph or centrilobular micronodules on chest CT images",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Coarse crackles",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        FEV",
"        <sub>",
"         1",
"        </sub>",
"        /FVC less than 70 percent and PaO",
"        <sub>",
"         2",
"        </sub>",
"        less than 80 mmHg (10.64 kPa)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Titer of cold agglutinins of 64 or greater",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Lung biopsy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Lung biopsy showing inflammation centered on the respiratory bronchioles with a transmural infiltrate composed of lymphocytes, plasma cells, and distinctive lipid-laden \"foamy\" macrophages, also known as foam cells*",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <p>",
"     &nbsp;",
"    </p>",
"    <div class=\"footnotes\">",
"     FEV",
"     <sub>",
"      1",
"     </sub>",
"     /FVC: forced expiratory volume in one second/forced vital capacity; PaO",
"     <sub>",
"      2",
"     </sub>",
"     : arterial tension for oxygen.",
"     <br>",
"      * In regions where diffuse panbronchiolitis is endemic (eg, East Asia), a lung biopsy is not needed if the patient fulfills the first three criteria and at least two of the second three.",
"     </br>",
"    </div>",
"    <div class=\"reference\">",
"     Data from: Kudoh S, Keicho N. Diffuse panbronchiolitis. Clin Chest Med 2012; 33:297.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
