var title_f33_35_34363="Dx neuropsychiatric lupus";
var content_f33_35_34363=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=RHEUM%2F77325&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=RHEUM%2F77325&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Diagnostic approach to manifestations of neuropsychiatric lupus",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Sequential evaluation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Confirm diagnosis of lupus according to ARA criteria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Careful history and physical examination - exclude systemic illness and medications as confounding variables",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1_start\">",
"        For specific symptoms or signs:",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Stroke - CT scan, blood tests for coagulopathy (including lupus anticoagulant), MRI, echocardiogram, carotids ultrasonography",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Seizures - EEG",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Neuropathy - EMG",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Psychosis - MRI, EEG, lumbar puncture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Cognitive abnormalities - Psychometric testing (to differentiate organic from",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        psychosocial disease), MRI, EEG, blood tests for coagulopathy, antibrain antibody",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Anxiety or depression - Psychometric testing (to differentiate organic from",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        psychosocial disease)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"sublist1\">",
"        Meningitis/fever - lumbar puncture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle1_single\">",
"        Monitoring",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        If patient improves: monitor history and physical examination&nbsp;",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        If patient gets no better or worse: MRI, LP",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
