"  <div class=\"headingAnchor\" id=\"references\">",
"   <h1>",
"    REFERENCES",
"   </h1>",
"   <ol id=\"reference\">",
"    <li>",
"     <a class=\"nounderline abstract\" href=\"mobipreview.htm?33/36/34376/abstract/1\">",
