" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Psychotherapy for obsessive-compulsive disorder",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?33/37/34391/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?33/37/34391/contributors\">",
"     Jonathan Abramowitz, PhD",
