var title_f33_39_34428="Stepwise diagnostic evaluation for cardiac sarcoidosis";
var content_f33_39_34428=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=CARD%2F85756&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=CARD%2F85756&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Stepwise diagnostic evaluation for cardiac sarcoidosis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <colgroup span=\"3\" width=\"33%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td rowspan=\"2\">",
"        <p>",
"         <strong>",
"          Step 1",
"         </strong>",
"        </p>",
"        Initial evaluation for cardiac disease in all patients with sarcoidosis",
"       </td>",
"       <td>",
"        History and physical examination",
"       </td>",
"       <td>",
"        Findings may include dyspnea, dizziness, bradycardia, ventricular or atrial arrhythmia, although some patients are asymptomatic and have no physical findings",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Electrocardiogram",
"       </td>",
"       <td>",
"        Abnormal: heart block",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"2\">",
"        <p>",
"         <strong>",
"          Step 2",
"         </strong>",
"        </p>",
"        Initial noninvasive evaluation for patients with suspected cardiac disease",
"       </td>",
"       <td>",
"        Holter monitor",
"       </td>",
"       <td>",
"        Abnormal: &gt;100 extra ventricular beats",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Echocardiogram",
"       </td>",
"       <td>",
"        Abnormal: hypokinesia, aneurysmal dilatation",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td rowspan=\"3\">",
"        <p>",
"         <strong>",
"          Step 3",
"         </strong>",
"        </p>",
"        Additional noninvasive imaging in patients with suspected cardiac disease: CMR plus PET are suggested if available",
"       </td>",
"       <td>",
"        Cardiovascular magnetic resonance (CMR) imaging",
"       </td>",
"       <td>",
"        Useful in detecting granulomatous inflammation (more sensitive than gallium, thallium, or technetium scans)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        FDG PET",
"       </td>",
"       <td>",
"        Useful in assessing granulomas or ischemia (more sensitive than gallium, thallium, or technetium scans)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Radionuclide (gallium, thallium, technetium sestamibi) scans",
"       </td>",
"       <td>",
"        Useful in assessing granulomas or ischemia",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <p>",
"         <strong>",
"          Step 4",
"         </strong>",
"        </p>",
"        In selected patients",
"       </td>",
"       <td>",
"        Coronary angiography",
"       </td>",
"       <td>",
"        Useful in excluding coronary artery disease when noninvasive testing reveals regional abnormalities which may be caused by either coronary disease or sarcoidosis",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <p>",
"         <strong>",
"          Step 5",
"         </strong>",
"        </p>",
"        In selected patients",
"       </td>",
"       <td>",
"        Endomyocardial biopsy",
"       </td>",
"       <td>",
"        Only if the patient has no histological confirmation from any other source",
"       </td>",
"      </tr>",
"      <tr class=\"divider_top\">",
"       <td>",
"        <p>",
"         <strong>",
"          Step 6",
"         </strong>",
"        </p>",
"        In selected patients",
"       </td>",
"       <td>",
"        Electrophysiologic study",
"       </td>",
"       <td>",
"        In selected patients with high risk features (eg, abnormalities on CMR or PET imaging) who lack established indications for implantable-cardioverter defibrillator (ICD) placement",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Adapted and expanded from: Schulte W, Kirsten D, Drent M, Costabel U. Cardiac involvement in sarcoidosis. Eur Respir Mon 2005; 32:130.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
