" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Treatment of dementia",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?33/4/33865/contributors\">",
