" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Treatment of Chlamydia trachomatis infection",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?33/44/34505/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?33/44/34505/contributors\">",
"     Jeanne Marrazzo, MD, MPH",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?33/44/34505/contributors\">",
"     Section Editor",
"    </a>",
