var title_f33_47_34557="Evaluation of transverse myelitis";
var content_f33_47_34557=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F65585&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F65585&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Potential medical work-up for suspected acute transverse myelitis",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Indicative signs and symptoms",
"       </td>",
"       <td class=\"subtitle1\">",
"        Suggested evaluation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Infectious etiology",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Fever",
"       </td>",
"       <td>",
"        CSF Gram's stain and bacterial culture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Meningismus",
"       </td>",
"       <td>",
"        CSF PCR: HSV-1, HSV-2, HHV-6, VZV, CMV, EBV, enteroviruses, HIV",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Rash",
"       </td>",
"       <td>",
"        CSF viral culture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Concurrent systemic infection",
"       </td>",
"       <td>",
"        CSF acid-fast bacilli smear and tuberculous culture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Immunocompromised state",
"       </td>",
"       <td>",
"        CSF HSV, VZV, and HTLV-1 antibodies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Recurrent genital infection",
"       </td>",
"       <td>",
"        CSF anti-",
"        <em>",
"         Borrelia burgdorferi",
"        </em>",
"        antibodies",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Symptoms of zoster radiculopathy",
"       </td>",
"       <td>",
"        CSF VDRL",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Adenopathy",
"       </td>",
"       <td>",
"        CSF India ink and fungal culture",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"4\">",
"        Residence in area endemic for parasitic infections",
"       </td>",
"       <td>",
"        Chest radiograph",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Serology for antibodies to HSV, VZV, HTLV-1,",
"        <em>",
"         B. burgdorferi",
"        </em>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Serology for hepatitis A, B, C, and",
"        <em>",
"         Mycoplasma",
"        </em>",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Consider serology for parasites",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Systemic inflammatory disease (vasculitis, collagen vascular diseases, mixed connective tissue disease)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Rash",
"       </td>",
"       <td>",
"        Serum ACE",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Oral or genital ulcers",
"       </td>",
"       <td>",
"        Auto-antibodies: ANA, ds-DNA, SS-A (Ro), SS-B (La), Sm (Smith), RNP",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Adenopathy",
"       </td>",
"       <td>",
"        Complement levels",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Livedo reticularis",
"       </td>",
"       <td>",
"        Urinalysis with microscopic analysis for hematuria",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Serositis",
"       </td>",
"       <td>",
"        Lip/salivary gland biopsy",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Photosensitivity",
"       </td>",
"       <td>",
"        Chest CT",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Inflammatory arthritis",
"       </td>",
"       <td>",
"        Schirmer's test",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Erythema nodosum",
"       </td>",
"       <td>",
"        Chest radiograph",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Xerostomia",
"       </td>",
"       <td rowspan=\"7\">",
"        Anti-phospholipid antibodies (anti-cardiolipin antibodies, Russel viper venom time, partial thromboplastin time)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Keratitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Conjunctivitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Contractures or thickening of skin",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Anemia/leukopenia/thrombocytopenia",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Raynaud's phenomenon",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        History of arterial and venous thrombosis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        MS",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Previous demyelination event",
"       </td>",
"       <td>",
"        Brain MRI",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Incomplete deficit clinically with MRI abnormality &le;2 spinal segments and &lt;50 percent of cord diameter",
"       </td>",
"       <td rowspan=\"2\">",
"        Evoked potentials",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        CSF oligoclonal bands",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Neuromyelitis optica (Devic's disease)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Optic neuritis",
"       </td>",
"       <td>",
"        Evoked potentials",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Normal brain MRI",
"       </td>",
"       <td>",
"        Brain MRI (usually negative)",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Presence of multiple autoantibodies, of the type listed above or others",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"2\">",
"        Idiopathic transverse myelitis",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        No clinical or paraclinical features suggestive of another diagnostic category",
"       </td>",
"       <td>",
"        Evoked potentials",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Electromyography/nerve conduction velocity",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     HSV: herpes simplex virus; HHV: human herpes virus; VZV: varicella zoster virus; CMV: cytomegalovirus; EBV: Epstein-Barr virus; HTLV-1: human T-cell lymphotropic virus-1; VDRL: Veneral Disease Research Laboratory; ACE: angiotensin-converting enzyme; ANA: anti-nuclear antibodies; ds: double-stranded; RNP: ribonucleoprotein.",
"    </div>",
"    <div class=\"reference\">",
"     Reproduced with permission from: Transverse Myelitis Consortium Working Group. Proposed diagnostic criteria and nosology of acute transverse myelitis. Neurology 2002; 59:499. Copyright &copy; 2002 Lippincott Williams &amp; Wilkins.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
var script_f33_47_34557=[""].join("\n");
