" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Corneal abrasions and corneal foreign bodies",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?33/50/34600/contributors\">",
