var title_f33_50_34603="Resp patterns and ABG in Coma";
var content_f33_50_34603=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=NEURO%2F71388&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=NEURO%2F71388&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Ventilatory and arterial blood gas patterns in coma",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Breathing pattern",
"       </td>",
"       <td class=\"subtitle1\">",
"        Metabolic pattern",
"       </td>",
"       <td class=\"subtitle1\">",
"        pH, PaCO2, HCO3",
"       </td>",
"       <td class=\"subtitle1\">",
"        Specific conditions",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hyperventilation",
"       </td>",
"       <td>",
"        Metabolic acidosis",
"       </td>",
"       <td>",
"        pH &lt;7.3, PaCO2 &lt;30 mmHg, HCO3 &lt;17 mmol/L",
"       </td>",
"       <td>",
"        Uremia, diabetic ketoacidosis, lactic acidosis, salicylates, methanol, ethylene glycol",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hyperventilation",
"       </td>",
"       <td>",
"        Respiratory alkalosis",
"       </td>",
"       <td>",
"        pH &gt;7.45, PaCO2 &lt;30 mmHg, HCO3 &gt;17 mmol/L",
"       </td>",
"       <td>",
"        Hepatic failure, acute sepsis, acute salicylate intoxication, cardiopulmonary states with hypoxemia, psychogenic causes",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hypoventilation",
"       </td>",
"       <td>",
"        Respiratory acidosis",
"       </td>",
"       <td>",
"        pH &lt;7.35 (if acute), PaCO2 &gt;90 mmHg, HCO3 &gt;17 mmol/L",
"       </td>",
"       <td>",
"        Respiratory failure from central (eg, brain or spinal cord) or peripheral nervous system disease, chest conditions or deformities. Coma only with severe hypercarbia.",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Hypoventilation",
"       </td>",
"       <td>",
"        Metabolic alkalosis",
"       </td>",
"       <td>",
"        pH&gt; 7.45, PaCO2 &gt;45 mmHg, HCO3 &gt;30 mmol/L",
"       </td>",
"       <td>",
"        Vomiting, alkali ingestion. Usually no impairment of consciousness; if so, suspect psychogenic unresponsiveness or additional cause.",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
