var content_f33_50_34604=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PEDS%2F68769&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PEDS%2F68769&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=2\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Differential diagnosis of foot and ankle pain in the young athlete",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\">",
"     <tbody>",
"      <tr>",
"       <td class=\"subtitle1\">",
"        Condition",
"       </td>",
"       <td class=\"subtitle1\">",
"        Mechanism of injury",
"       </td>",
"       <td class=\"subtitle1\">",
"        Location of pain",
"       </td>",
"       <td class=\"subtitle1\">",
"        Radiograph",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\" rowspan=\"1\">",
"        Talar",
"pain",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        OCD talus, posteromedial",
"       </td>",
"       <td>",
"        Inversion with plantarflexion",
"       </td>",
"       <td>",
"        Postermedial talus in dorsiflexion",
"       </td>",
"       <td>",
"        AP view of ankle, maximal plantar flexion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        OCD talus, anterolateral",
"       </td>",
"       <td>",
"        Inversion with dorsiflexion",
"       </td>",
"       <td>",
"        Anterolateral talus in plantarflexion",
"       </td>",
"       <td>",
"        Mortise view in ankle dorsiflexion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Stress fracture, talus",
"       </td>",
"       <td>",
"        Chronic stress (running, dancing)",
"       </td>",
"       <td>",
"        Rearfoot pain with activity",
"       </td>",
"       <td>",
"        AP, lateral, and mortise views of ankle; may be better",
"seen with MRI or CT",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"subtitle2_left\" colspan=\"4\" rowspan=\"1\">",
"        Lateral",
"ankle pain",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Ankle sprain (lateral ligaments)",
"       </td>",
"       <td>",
"        Inversion of ankle",
"       </td>",
"       <td>",
"        ATFL, PTFL, CFL",
"       </td>",
"       <td>",
"        AP, lateral, and mortise views of ankle",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        Sinus tarsi syndrome",
"       </td>",
"       <td>",
"        Severe, recurrent ankle inversion sprain",
"       </td>",
"       <td>",
"        Lateral ankle (posterior and inferior to ATFL)",
"       </td>",
"       <td>",
"        Stress radiographs of the ankle",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"4\">",
"        Peroneal tendon injuries",
"       </td>",
"       <td>",
"        Acute ankle sprain",
"       </td>",
"       <td rowspan=\"4\">",
"        Peroneal tendons (superior to lateral malleolus)",
"       </td>",
"       <td rowspan=\"4\">",
"        AP, lateral, and mortise views of the ankle (to exclude",
"bony injury); Consider MRI",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Chronic ankle instability",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Peroneal",
"subluxation",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Overuse",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\" rowspan=\"2\">",
"        Os peroneum",
"       </td>",
"       <td rowspan=\"2\">",
"        &nbsp;",
"       </td>",
"       <td>",
"        Lateral plantar cuboid",
"       </td>",
"       <td rowspan=\"2\">",
"        AP, lateral, and mortise views of the ankle; AP,",
"oblique views of the foot",
"       </td>",
"      </tr>",
"      <tr>",
"       <td>",
"        Calcaneocuboid articulartion",
"       </td>",
"      </tr>",
"      <tr>",
"       <td class=\"indent1\">",
"        L4 radiculopathy",
"       </td>",
"       <td>",
"        Disc herniation",
"       </td>",
"       <td>",
"        Quadriceps",
"       </td>",
"       <td>",
"        Consider MRI spine (after EMG/NCS)",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"     OCD: osteochondritis dissecans; AP: anteroposterior; MRI: magnetic resonance imaging; CT: computed tomography; ATFL: anterior talofibular ligament; PTFL: posterior talofibular ligament; CFL: calcaneofibular ligament; EMG: electromyography; NCS: nerve conduction studies.",
"    </div>",
"    <div class=\"reference\">",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
