" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Procedural sedation in adults",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?33/51/34618/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?33/51/34618/contributors\">",
"     Robert L Frank, MD, FACEP",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?33/51/34618/contributors\">",
"     Section Editor",
