" <div id=\"topicContent\">",
"  <div id=\"topicTitle\">",
"   Hematopoietic support after hematopoietic cell transplantation",
"  </div>",
"  <div id=\"topicContributors\">",
"   <div>",
"    <a id=\"authors\">",
"    </a>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?33/54/34664/contributors\">",
"     Author",
"    </a>",
"    <br/>",
"    <a class=\"contributor contributor_credentials\" href=\"mobipreview.htm?33/54/34664/contributors\">",
"     Robert S Negrin, MD",
"    </a>",
"    <br/>",
"   </div>",
"   <div>",
"    <a class=\"contributor contributor_credentials contributorType\" href=\"mobipreview.htm?33/54/34664/contributors\">",
"     Section Editor",
"    </a>",
"    <br/>",
