var title_f33_54_34667="Medical probs breastfeed PI";
var content_f33_54_34667=[" <div id=\"graphicsToolbar\">",
"  <div id=\"graphicsCopy\">",
"   &copy;2013 UpToDate",
"   <sup>",
"    &reg;",
"   </sup>",
"  </div>",
"  <div id=\"graphicsLinks\">",
"   <a href=\"?imageKey=PI%2F72083&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\">",
"    <img alt=\"Print this page\" src=\"./../images/icn_print.myextg\" title=\"Print this page\"/>",
"   </a>",
"   <a class=\"icontxt textLink\" href=\"?imageKey=PI%2F72083&amp;source=image_view&amp;view=print&amp;elapsedTimeMs=1\" onclick=\"\" title=\"Print this page\">",
"    Print",
"   </a>",
"   <a class=\"etacLink\" href=\"#\">",
"    <img alt=\"Email graphic(s)\" src=\"./../images/icn_email.myextg\" title=\"Email graphic(s)\"/>",
"   </a>",
"   <a class=\"icontxt textLink etacLink\" href=\"#\" title=\"Email graphic(s)\">",
"    Email",
"   </a>",
"  </div>",
" </div>",
" <div class=\"graphic\">",
"  <div class=\"figure\">",
"   <div class=\"ttl\">",
"    Medical problems and breastfeeding safety",
"   </div>",
"   <div class=\"cntnt\">",
"    <table cellspacing=\"0\" class=\"container\">",
"     <colgroup span=\"2\" width=\"50%\">",
"     </colgroup>",
"     <tbody>",
"      <tr>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Breastfeeding is not recommended for women or infants with the following conditions:",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Women who have human immunodeficiency virus (HIV)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Women with human T-cell lymphotropic virus type I or II (HTLV)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Women being treated with chemotherapy or radiation therapy",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Women who abuse drugs or alcohol",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Women with herpes simplex lesions on the breasts (ok to breastfeed if one breast has no lesions)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Infants with classic galactosemia (galactose 1-phosphate uridyltransferase deficiency)",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"       <td class=\"container\">",
"        <table cellspacing=\"0\">",
"         <tbody>",
"          <tr>",
"           <td class=\"subtitle1_single\">",
"            Breastfeeding is encouraged for women or infants with the following conditions:",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Women with hepatitis (A, B, or C)",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Women with past or current cytomegalovirus (CMV) infection",
"           </td>",
"          </tr>",
"          <tr>",
"           <td>",
"            Infants who develop jaundice",
"           </td>",
"          </tr>",
"         </tbody>",
"        </table>",
"       </td>",
"      </tr>",
"     </tbody>",
"    </table>",
"   </div>",
"   <div class=\"lgnd\">",
"    <div class=\"footnotes\">",
"    </div>",
"    <div class=\"reference\">",
"     Data from: American Academy of Pediatrics. Pediatric Nutrition Handbook. 6th edition, 2009.",
"    </div>",
"   </div>",
"  </div>",
" </div>",
"</div>"].join("\n");
